import React, { Component } from 'react'
import Navbar from './components/Navbar'
import Newsitem from './components/Newsitem'

export class App extends Component {

  constructor(){
    super()
    console.log("constructor")
    this.state = {
      article:this.article,
      loading:false,
      page:1
    }
  }
    async componentDidMount() { 
      const url = "https://newsapi.org/v2/top-headlines?country=us&apiKey=3eb0372fe9a140e18a95c412c4524f12&page=1";
      const data = await fetch(url)
      const result = await data.json();
      console.log(result)
      this.setState({
        article:result.articles
      })


     }

  render() {
    return (
      <>
      <Navbar/>
      <div className='container my-3'>

      <h2 className='text-center'> newsmonkey - top Headlines</h2>
      <div className="row">
      {this.state.article?.map((item,index)=>{
        const {title,description,urlToImage,url} = item

        return (<div className="col-md-4">
          <Newsitem title={!title?"":title.slice(0,20)} desc={!description?"No Description":description.slice(0,85)} key={index} imgurl={!urlToImage?"https://static.thenounproject.com/png/3674270-200.png":urlToImage} newsurl={url}/>
        </div>)

        })}

      </div>
      <div className="container d-flex justify-content-between">
        <button className="btn btn-dark">&larr; previous</button>
        <button className="btn btn-dark">Next &rarr;</button>

      </div>
      </div>
      </>
    )
  }
}

export default App
