import React, { Component } from 'react'
import Navbar from './components/Navbar'
import Newsitem from './components/Newsitem'

export class App extends Component {

  constructor(){
    super()
    console.log("constructor")
    this.state = {
      article:this.article,
      loading:false,
    }
  }
    async componentDidMount() { 
      const url = "https://newsapi.org/v2/top-headlines?country=us&apiKey=3eb0372fe9a140e18a95c412c4524f12";
      const data = await fetch(url)
      const result = await data.json();
      console.log(result)
      this.setState({
        article:result.article
      })


     }

  render() {
    return (
      <>
      <Navbar/>
      <div className='container my-3'>

      <h2 className='text-center'> newsmonkey - top Headlines</h2>
      <div className="row">
      {this.state.article?.map((item,index)=>{

        return (<div className="col-md-4">
          <Newsitem title={item.title.slice(0,88)} desc={item.description.slice(0,88)} key={index} imgurl={item.urlToImage} newsurl={item.url}/>
        </div>)

        })}

      </div>
      </div>
      </>
    )
  }
}

export default App
